import React, { useState } from 'react';
import axios from 'axios';
import './recommendationForm.css';

const RecommendationForm = () => {
    const [skills, setSkills] = useState('');
    const [recommendations, setRecommendations] = useState([]);
    const [error, setError] = useState('');

    const prompts = [
        "Write a to-do list for a personal project or task",
        "Generate an email to reply to a job offer",
        "Summarize this article or text for me in one paragraph",
        "How does AI work in a technical capacity",
    ];

    const handlePromptClick = (prompt) => {
        setSkills(prompt);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const skillsArray = skills.split(',').map(skill => skill.trim()).filter(skill => skill);
            if (skillsArray.length === 0) {
                setError('Please enter at least one skill.');
                return;
            }

            const response = await axios.post('http://localhost:3001/recommend/get_recommendations', {
                skills: skillsArray
            });
            setRecommendations(prev => [...prev, response.data.recommendations]);
            setSkills('');
        } catch (err) {
            console.error('Error fetching recommendations:', err);
            setError('Failed to fetch recommendations. Please try again.');
        }
    };

    const handleRefreshPrompts = () => {
        setSkills('');
    };

    return (
        <div className="recommendation-form-container">
            <div className="recommendations-container">
                {recommendations.length > 0 ? (
                    recommendations.map((recommendation, index) => (
                        <div key={index} className="recommendation-item">
                            <h3>Recommended Job Roles:</h3>
                            <p>{recommendation}</p>
                        </div>
                    ))
                ) : (
                    <p className="no-recommendations">No recommendations yet. Start by entering a prompt.</p>
                )}
            </div>
            <div className="fixed-input-container">
                <div className="prompt-cards">
                    {prompts.map((prompt, index) => (
                        <div
                            key={index}
                            className="prompt-card"
                            onClick={() => handlePromptClick(prompt)}
                        >
                            <p>{prompt}</p>
                        </div>
                    ))}
                </div>

                <button className="refresh-btn" onClick={handleRefreshPrompts}>ðŸ”„ Refresh Prompts</button>
                <form className="input-container d-flex" onSubmit={handleSubmit} >
                    <div className="grid gap-10 grid-cols-1 md:grid-cols-10">
                        <div className="col-span-9">
                            <textarea
                                placeholder="Ask whatever you want...."
                                value={skills}
                                onChange={(e) => setSkills(e.target.value)}
                                className="w-full p-2 border border-gray-300 rounded"
                            />
                        </div>
                        <div className="col-span-1 flex justify-center items-center">
                            <div className="input-actions">
                                <button type="submit" className="submit-btn p-2 bg-blue-500 text-white rounded hover:bg-blue-600">
                                    âž”
                                </button>
                            </div>
                        </div>
                    </div>

                    {error && <p className="error-text text-red-500">{error}</p>}
                </form>
            </div>
        </div>
    );
};

export default RecommendationForm;
