import React, { useState } from 'react';
import axios from 'axios';
import './recommendationForm.css';

const RecommendationForm = () => {
    const [skills, setSkills] = useState('');
    const [recommendations, setRecommendations] = useState('');
    const [error, setError] = useState('');

    const prompts = [
        "Write a to-do list for a personal project or task",
        "Generate an email to reply to a job offer",
        "Summarize this article or text for me in one paragraph",
        "How does AI work in a technical capacity",
    ];

    // Handle prompt selection
    const handlePromptClick = (prompt) => {
        setSkills(prompt); // Set the clicked prompt as the value in the text area
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setRecommendations('');

        try {
            const skillsArray = skills.split(',').map(skill => skill.trim()).filter(skill => skill);
            if (skillsArray.length === 0) {
                setError('Please enter at least one skill.');
                return;
            }

            const response = await axios.post('http://localhost:3001/recommend/get_recommendations', {
                skills: skillsArray
            });

            // Assuming the response contains HTML content in recommendations
            setRecommendations(response.data.recommendations);
        } catch (err) {
            console.error('Error fetching recommendations:', err);
            setError('Failed to fetch recommendations. Please try again.');
        }
    };

    // Refresh prompts logic (optional, here it's just resetting the text area)
    const handleRefreshPrompts = () => {
        setSkills(''); // Clear skills input on refresh
    };

    return (
        <div className="recommendation-form-container">
            {/* Greeting and Title */}
            <div className="header">
                <h1>Hi there, <span className="name-highlight">John</span></h1>
                <h2>What <span className="text-highlight">would you like to know?</span></h2>
                <p>Use one of the most common prompts below or use your own to begin</p>
            </div>

            {/* Prompt Cards */}
            <div className="prompt-cards">
                {prompts.map((prompt, index) => (
                    <div 
                        key={index} 
                        className="prompt-card"
                        onClick={() => handlePromptClick(prompt)}
                    >
                        <p>{prompt}</p>
                    </div>
                ))}
            </div>

            <button className="refresh-btn" onClick={handleRefreshPrompts}>ðŸ”„ Refresh Prompts</button>

            {/* Input Section */}
            <form className="input-container" onSubmit={handleSubmit}>
                <textarea
                    placeholder="Ask whatever you want...."
                    value={skills}
                    onChange={(e) => setSkills(e.target.value)} // Keeps the current input in the text area
                />
                {error && <p className="error-text">{error}</p>}

                <div className="input-actions">
                    <button type="button" className="attachment-btn">Add Attachment</button>
                    <button type="button" className="image-btn">Use Image</button>
                    <button type="submit" className="submit-btn">âž”</button>
                </div>
            </form>

            {/* Display Recommendations */}
            {recommendations && (
                <div className="recommendations-output">
                    <h3>Recommended Job Roles:</h3>
                    <p>{recommendations}</p>
                </div>
            )}
        </div>
    );
};

export default RecommendationForm;
