import React, { useState } from 'react';
import axios from 'axios';
import DOMPurify from 'dompurify'; // Assuming you are using DOMPurify
import marked from 'marked'; // Assuming you are using marked for markdown rendering
import './recommendationForm.css'; // Importing CSS for styling

const RecommendationForm = () => {
    const [skills, setSkills] = useState('');
    const [recommendations, setRecommendations] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setRecommendations('');

        try {
            const skillsArray = skills.split(',').map(skill => skill.trim()).filter(skill => skill);
            if (skillsArray.length === 0) {
                setError('Please enter at least one skill.');
                return;
            }

            const response = await axios.post('http://localhost:3001/recommend/get_recommendations', {
                skills: skillsArray
            });

            setRecommendations(response.data.recommendations);
        } catch (err) {
            console.error('Error fetching recommendations:', err);
            setError('Failed to fetch recommendations. Please try again.');
        }
    };

    const htmlContent = marked(recommendations);
    const sanitizedHTML = DOMPurify.sanitize(htmlContent);

    return (
        <div className="recommendation-form-container">
            <div className="recommendation-form-wrapper">
                <h1 className="recommendation-title">Skill-Based Job Recommendations</h1>
                <form onSubmit={handleSubmit} className="recommendation-form">
                    <label htmlFor="skills" className="form-label">Enter Skills (comma-separated):</label>
                    <input
                        id="skills"
                        type="text"
                        className="form-input"
                        value={skills}
                        onChange={(e) => setSkills(e.target.value)}
                        placeholder="e.g., JavaScript, Node.js, React"
                    />
                    <button type="submit" className="submit-btn">Get Recommendations</button>
                </form>
                {error && <p className="error-message">{error}</p>}
                {recommendations && (
                    <div className="recommendation-results">
                        <h2 className="recommendation-subtitle">Recommended Job Roles:</h2>
                        <div className="recommendation-list" dangerouslySetInnerHTML={{ __html: sanitizedHTML }} />
                    </div>
                )}
            </div>
        </div>
    );
};

export default RecommendationForm;
